def _get_option_with_source(
        option: Optional[str],
        envname: str,
    ) -> Union[Tuple[str, str], Tuple[None, None]]:
        if option:
            return option, "option"
        if envname in os.environ:
            return os.environ[envname], "env"
        cfgval = early_config.getini(envname)
        if cfgval:
            return cfgval, "ini"
        return None, None
